import { useState } from 'react';

const Planet = props => {
  const {name, radius, color, velocity, orbitRadius} = props;
  const [starX, setStarX] = useState(0);
  const [starY, setStarY] = useState(0);
  let theta = Math.random() * Math.PI * 2;
  let x, y;

  const update = ctx => {
    theta += velocity;

    x = star.x + orbitRadius * Math.cos(theta);
    y = star.y + orbitRadius * Math.sin(theta);

    draw(ctx);
  }

  const draw = ctx => {
    ctx.beginPath();
    ctx.arc(x, y, radius, 0, Math.PI * 2, false);
    ctx.fillStyle = color;
    ctx.fill();
    ctx.closePath();
  }
}
