import { Input, List } from './CustomizaztionMenu.js';
import { useRadioState, useRadioDispatch } from './contexts/RadioContext.js';
import styled from 'styled-components';

const RadioBlock = styled.div`
  position: relative;
  display: flex;
  flex-direction: column;
  background-color: var(--menu-sub);
  border-radius: 8px;
  margin-bottom: 18px;
  padding: 15px 15px 5px 15px;
  width: 160px;
  box-shadow: 2px 2px 8px var(--shadow);
`

export default function Radio({name, checked, id}) {
  const dispatch = useRadioDispatch();

  const onChange = () => {
    dispatch({
      type: 'RADIO',

    })
  }

  return (
    <List>
      <label className="label" htmlFor="name">
        <Input 
          type="checkbox" 
          id="name"
          checked={checked}
          onChange={onChange}
        >
        </Input>

        <span className="slider"></span>

        <span className="text">{name}</span>
      </label>
    </List>
  );
}
