import {useRadioState, useRadioDispatch} from './contexts/RadioContext.js';
import {useToggleState, useToggleDispatch} from './contexts/ToggleContext.js';
import Toggle from './Toggle.js';
import RadioList from './RadioList.js';
import styled, {css} from 'styled-components';
import {colors} from './Colors.js';

const CustomizationMenuBlock = styled.div`
  position: absolute;
  top: 30px;
  right: 15px;
  padding: 0;
  margin: 0;
  padding: 15px;
  background-color: colors[mine];
  border-radius: 8px;
  box-shadow: 4px 4px 12px rgb(38, 43, 56);
`;

const MenuTitle = styled.h1`
  color: colors[menu-title];
  font-size: 1.1rem;
  margin-top: 0;
  margin-bottom: 25px;
  margin-left: 3px;
`;

const Separator = styled.div`
  position: absolute;
  width: 220px;
  height: 1px;
  background-color: rgb(231, 233, 236);
  top: 75px;
  right: 15px;
  padding: 0;
  margin-bottom: 15px;
`;

export const Input = styled.input`
  opacity: 0;
  width: 0;
  height: 0;
  position: absolute;
  cursor: pointer;
`;

export const List = styled.li`
  list-style: none;
`;

export default function CustomizationMenu() {
  const radioOptions = useRadioState();
  const toggleOptions = useToggleState();

  return (
    <CustomizationMenuBlock>
      <ul>
        <MenuTitle>Animation Effects</MenuTitle>
        <Separator />
        {toggleOptions.map((toggle) => (
          <Toggle
            optionName={toggle.name}
            checked={toggle.checked}
            key={toggle.id}
          />
        ))}
        {radioOptions.map((radio) => (
          <RadioList
            radioType={radio.type}
            radioOptions={radio.options}
            key={radio.id}
          />
        ))}
      </ul>
    </CustomizationMenuBlock>
  );
}
