import { useContext, createContext, useReducer } from 'react';

const initialCustomOptions = [
    {
      name: 'dragTypes',
      'no-drag': true,
      'basic-drag': false,
      'elastic-drag': false,
    },
    {
      name: 'shadowTypes',
      'no-shadow': true,
      'rectangular-shadow': false,
      'round-shadow': false,
    },
    {
      name: 'background',
      checked: false,
    },
    {
      name: 'planet-art',
      checked: false,
    },
    {
      name: 'orbit-path',
      checked : false,
    },
];

function customOptionReducer(state, action) {
  switch(action.type) {
    case 'TOGGLE':
      return state.map(option =>
        option.name === action.name ? { ... option, checked: !option.name.checked } : option
      );
    case 'RADIO':
      return state.map(option =>
        option.radio
      );
    default:
      throw new Error(`Unhandled action type: ${action.type}`);
  }
}

const CustomOptionStateContext = createContext();
const CustomOptionDispatchContext = createContext();

export function CustomOptionProvider({children}) {
  const [state, dispatch] = useReducer(customOptionReducer, initialCustomOptions);

  return (
    <CustomOptionStateContext.Provider value={state}>
      <CustomOptionDispatchContext.Provider value={dispatch}>
        {children}
      </CustomOptionDispatchContext.Provider>
    </CustomOptionStateContext.Provider>
  );
}

export function useCustomOptionState() {
  const context = useContext(CustomOptionStateContext);
  if (!context) {
    throw new Error('Cannot find CustomOptionProvider');
  }
  return context;
}

export function useCustomOptionDispatch() {
  const context = useContext(CustomOptionDispatchContext);
  if (!context) {
    throw new Error('Cannot find CustomOptionProvider');
  }
  return context;
}
