import { useRadioState, useRadioDispatch } from './RadioContext.js';
import { useToggleState, useToggleDispatch } from './ToggleContext.js';
import Toggle from './Toggle.js';
import RadioList from './RadioList.js';

export default function CustomizationMenu() {
  const radioOptions = useRadioState();
  const radioDispatch = useRadioDispatch();
  const toggleOptions = useToggleState();


  return (
    <ul id="toggleMenu">
      <h1>Animation Effects</h1>
      <div class="separator"></div>
      {toggleOptions.map(toggle => (<Toggle />))}
      {radioOptions.map(radio => (<RadioList />))}
    </ul>
  );
}
