import { useRadioState, useRadioDispatch } from './contexts/RadioContext.js';
import { useToggleState, useToggleDispatch } from './contexts/ToggleContext.js';
import Toggle from './Toggle.js';
import RadioList from './RadioList.js';
import styled, {css} from 'styled-components';

const CustomizationMenuBlock = styled.div

export default function CustomizationMenu() {
  const radioOptions = useRadioState();
  const toggleOptions = useToggleState();


  return (
    <ul id="toggleMenu">
      <h1>Animation Effects</h1>
      <div className="separator"></div>
      {toggleOptions.map(toggle => (<Toggle optionName={toggle.name} checked={toggle.checked} key={toggle.id} />))}
      {radioOptions.map(radio => (<RadioList radioType={radio.type} radioOptions={radio.options} key={radio.id} />))}
    </ul>
  );
}
