import React from 'react';
import Canvas from './Canvas';

const Background = () => {
  const draw = (ctx, frameCount, stageWidth, stageHeight) => {
    ctx.fillStyle = createGradient(ctx, stageWidth, stageHeight);
    ctx.fillRect(0, 0, stageWidth, stageHeight);
  }

  const fillStars = (n) => {

  }


  const createGradient = (stageWidth, stageHeight, ctx) => {
    let gradient = ctx.createRadialGradient(
      stageWidth / 2,
      stageHeight / 2,
      stageWidth / 3,
      stageWidth / 2,
      stageHeight / 2,
      stageWidth / 1.5
    );

    gradient.addColorStop(0, '#214568');
    gradient.addColorStop(1, '#162133');

    return gradient;
  }

  return <Canvas draw={draw} />
}

export default Background;
