import { useRadioState, useRadioDispatch } from './RadioContext.js';

export default function Radio(name) {
  const dispatch = useRadioDispatch();

  return (
    <li>
    <label className="label" htmlFor="name">
      <input 
        type="checkbox" 
        id="name"
        checked={(name ==='no-drag' || name === 'no-shadow') ? true : false}
      >
      </input>

      <span className="slider"></span>

      <span className="text">{name}</span>
    </label>
  </li>
  );
}
