import React from 'react';
import SolarSystem from './components/SolarSystem.js';
import Background from './components/Background.js';
import CustomizationMenu from './components/CustomizaztionMenu.js';
import {RadioProvider} from './contexts/RadioContext.js';
import {ToggleProvider} from './contexts/ToggleContext.js';
import { planetInfo } from './consts/PlanetInfo.js';
import Planet from '../js/Planet.js';

const AppProvider = ({contexts, children}) =>
  contexts.reduce(
    (prev, context) =>
      React.createElement(context, {
        children: prev,
      }),
    children
  );

function App() {
  let planets = [];
  const createPlanets = () => {
    // console.log("createPlanets");
    let sun;
    let earth;

    for (let planet of planetInfo) {
      if (planet.name === 'sun') {
        let sunStar = {
          x: window.innerWidth/2,
          y: window.innerHeight/2,
        }
        sun = new Planet(planet.name, sunStar, planet.radius, planet.color, planet.velocity, planet.orbitRadius, planet.src);
        planets.push(sun);
      } else if (planet.name === 'earth') {
        earth = new Planet(planet.name, sun, planet.radius, planet.color, planet.velocity, planet.orbitRadius, planet.src);
        planets.push(earth);
      } else {
        if (planet.star === 'sun') {
          planets.push(new Planet(planet.name, sun, planet.radius, planet.color, planet.velocity, planet.orbitRadius, planet.src));
        } else {
          planets.push(new Planet(planet.name, earth, planet.radius, planet.color, planet.velocity, planet.orbitRadius, planet.src));
        }
      }
    }
  }
  createPlanets();

  return (
    <AppProvider contexts={[RadioProvider, ToggleProvider]}>
      <Background />
      <SolarSystem/>
      <CustomizationMenu />
    </AppProvider>
  );
}

export default App;
