import { useRadioState, useRadioDispatch } from './RadioContext.js';

export default function Radio(optionName) {
  return (
    <li>
    <label className="label" htmlFor="optionName">
      <input 
        type="checkbox" 
        id="optionName"
        checked={(optionName ==='no-drag' || optionName === 'no-shadow') ? true : false}
      >
      </input>

      <span className="slider"></span>

      <span className="text">{optionName}</span>
    </label>
  </li>
  );
}
