import { Input, List } from './CustomizaztionMenu.js';
import { useRadioState, useRadioDispatch } from './contexts/RadioContext.js';
import styled from 'styled-components';
import { colors } from './Colors.js';

const RadioLabel = styled.div`
  position: relative;
  display: block;
  cursor: pointer;
  width: 100%;
  margin: 5px 0 5px 0;
`

const RadioCheck = styled.input`
  position: absolute;
  height: 13px;
  width: 13px;
  border: 2px solid ${colors['default']};
  border-radius: 50%;
  &:hover {
    border-color: var(--hovered);
  }
`

const Text = styled.span`
  font-size: 0.9rem;
  position: relative;
  bottom: 1px;
  left: 28px;
  color: ${colors['default']};
`

export default function Radio({name, checked, id}) {
  const dispatch = useRadioDispatch();

  const onChange = () => {
    dispatch({
      type: 'RADIO',

    })
  }

  return (
    <List>
      <RadioLabel htmlFor="name">
        <Input 
          type="checkbox" 
          id="name"
          checked={checked}
          onChange={onChange}
        >
        </Input>

        <RadioCheck></RadioCheck>

        <span>{name}</span>
      </RadioLabel>
    </List>
  );
}
