import { useCustomOptionDispatch } from './RadioContext.js';

export default function Toggle(optionName) {
  const dispatch = useCustomOptionDispatch();

  const onToggle = (optionName) => {
    dispatch({
      type: 'TOGGLE',
      optionName,
    })
  }

  return (
    <li>
      <label className="label" htmlFor="optionName" onToggle={onToggle}>
        <input 
          type="checkbox" 
          id="optionName"
          checked={(optionName ==='no-drag' || optionName === 'no-shadow') ? true : false}
        >
        </input>

        <span className="slider"></span>

        <span className="text">{optionName}</span>
      </label>
    </li>
  );
}
