import { Input } from './CustomizaztionMenu.js';
import { useRadioState, useRadioDispatch } from './contexts/RadioContext.js';
import styled from 'styled-components';
import { colors } from './Colors.js';

const RadioLabel = styled.div`
  position: relative;
  display: block;
  cursor: pointer;
  width: 100%;
  margin: 5px 0 5px 0;
`

const RadioCheck = styled.input`
  position: absolute;
  height: 13px;
  width: 13px;
  border: 2px solid ${colors['default']};
  border-radius: 50%;
  &:after {
    content: "";
    position: absolute;
    display: none;
    top: 2px;
    left: 2px;
    width: 9px;
    height: 9px;
    background-color: ${colors['checked']};
    border-radius: 50%;
  }
`

const Text = styled.span`
  font-size: 0.9rem;
  position: relative;
  bottom: 1px;
  left: 28px;
  color: ${colors['default']};
`

const List = styled.li`
  list-style: none;
  &:hover {
    ${RadioCheck} {
      border-color: ${colors['hovered']};
    }
  }
  &:hover {
    ${Text} {
      color: ${colors['hovered']};
    }
  }
`

export default function Radio({name, checked, id}) {
  const dispatch = useRadioDispatch();

  const onChange = () => {
    dispatch({
      type: 'RADIO',

    })
  }

  return (
    <List>
      <RadioLabel htmlFor="name">
        <Input 
          type="checkbox" 
          id="name"
          checked={checked}
          onChange={onChange}
        >
        </Input>

        <RadioCheck></RadioCheck>

        <Text>{name}</Text>
      </RadioLabel>
    </List>
  );
}
