import { useContext, createContext, useReducer } from 'react';

const initialRadios = [
  {
    name: 'dragTypes',
    'no-drag': true,
    'basic-drag': false,
    'elastic-drag': false,
  },
  {
    name: 'shadowTypes',
    'no-shadow': true,
    'rectangular-shadow': false,
    'round-shadow': false,
  },
];

const initialToggles = {
  'background': false,
  'planet-art': false,
  'orbit-path': false,
};

function toggleReducer(state, action) {
  switch(action.type) {
    case 'TOGGLE':
      return state.map(option =>
        option.toggle[action.name] = !option.toggle[action.name]
      );
    case 'RADIO':
      return state.map(option =>
        option.radio
      );
    default:
      throw new Error(`Unhandled action type: ${action.type}`);
  }
}

const ToggleStateContext = createContext();
const ToggleDispatchContext = createContext();

export function CustomOptionProvider({children}) {
  const [state, dispatch] = useReducer(toggleReducer, initialToggles);

  return (
    <ToggleStateContext.Provider value={state}>
      <ToggleDispatchContext.Provider value={dispatch}>
        {children}
      </ToggleDispatchContext.Provider>
    </ToggleStateContext.Provider>
  );
}

export function useCustomOptionState() {
  const context = useContext(ToggleStateContext);
  if (!context) {
    throw new Error('Cannot find CustomOptionProvider');
  }
  return context;
}

export function useCustomOptionDispatch() {
  const context = useContext(ToggleDispatchContext);
  if (!context) {
    throw new Error('Cannot find CustomOptionProvider');
  }
  return context;
}
