import { useContext, createContext, useReducer } from 'react';

const initialRadio = [
    {
      name: 'dragTypes',
      'no-drag': true,
      'basic-drag': false,
      'elastic-drag': false,
    },
    {
      name: 'shadowTypes',
      'no-shadow': true,
      'rectangular-shadow': false,
      'round-shadow': false,
    },
];

function radioReducer(state, action) {
  switch(action.type) {
    case 'TOGGLE':
      return state.map(radio =>
        (radio.name === action.name) ? { ...radio, checked: !radio.name.checked } : radio
      );
    // case 'RADIO':
      // return state.map(option =>
        // (option.name === action.name) ? { 'no-drag': false, 'basic-drag': false, 'elastic-drag': false }
      // );
    default:
      throw new Error(`Unhandled action type: ${action.type}`);
  }
}

const RadioStateContext = createContext();
const RadioDispatchContext = createContext();

export function CustomOptionProvider({children}) {
  const [state, dispatch] = useReducer(radioReducer, initialRadio);

  return (
    <RadioStateContext.Provider value={state}>
      <RadioDispatchContext.Provider value={dispatch}>
        {children}
      </RadioDispatchContext.Provider>
    </RadioStateContext.Provider>
  );
}

export function useCustomOptionState() {
  const context = useContext(RadioStateContext);
  if (!context) {
    throw new Error('Cannot find CustomOptionProvider');
  }
  return context;
}

export function useCustomOptionDispatch() {
  const context = useContext(RadioDispatchContext);
  if (!context) {
    throw new Error('Cannot find CustomOptionProvider');
  }
  return context;
}
