import { Input, List } from './CustomizaztionMenu.js';
import { useRadioState, useRadioDispatch } from './contexts/RadioContext.js';
import styled from 'styled-components';

const RadioLabel = styled.div`
  position: relative;
  display: block;
  cursor: pointer;
  width: 100%;
  margin: 5px 0 5px 0;
`

export default function Radio({name, checked, id}) {
  const dispatch = useRadioDispatch();

  const onChange = () => {
    dispatch({
      type: 'RADIO',

    })
  }

  return (
    <List>
      <RadioLabel htmlFor="name">
        <Input 
          type="checkbox" 
          id="name"
          checked={checked}
          onChange={onChange}
        >
        </Input>

        <span className="slider"></span>

        <span className="text">{name}</span>
      </RadioLabel>
    </List>
  );
}
