import { useToggleDispatch } from './contexts/ToggleContext.js';
import { Input } from './CustomizaztionMenu.js';


export const List = styled.li`
  list-style: none;
`

export default function Toggle({optionName, checked, id}) {
  const dispatch = useToggleDispatch();

  const onToggle = (optionName) => {
    dispatch({
      type: 'TOGGLE',
      optionName,
    })
  }

  return (
    <List>
      <label className="label" htmlFor="optionName" >
        <Input type="checkbox" id="optionName" checked={checked} onChange={onToggle}></Input>
        <span className="slider"></span>
        <span className="text">{optionName}</span>
      </label>
    </List>
  );
}
