import React from 'react';
import { planetInfo } from './PlanetContext.js';
import Canvas from './Canvas.js';
import Planet from './Planet.js';

export default function SolarSystem() {
  let planets = [];
  for (let planet of planetInfo) {
    planets.push(new Planet(planet.name, planet.star, planet.radius, planet.color, planet.velocity, planet.orbitRadius));
  }

  const draw = (ctx, canvas, stageWidth, stageHeight) => {
    ctx.clearRect(0, 0, stageHeight, stageHeight);

    for (let planet of planets) {
      planet.update(ctx, canvas);
    }


  }


  return (
    <Canvas 
      draw={draw} 
      id='main' 
      type='dynamic'
    />
  );
}
