import { useRadioState, useRadioDispatch } from './contexts/RadioContext.js';

export default function Radio({name, checked, id}) {
  const dispatch = useRadioDispatch();

  const onChange = () => {
    dispatch({
      type: 'RADIO',

    })
  }

  return (
    <li>
      <label className="label" htmlFor="name">
        <input 
          type="checkbox" 
          id="name"
          checked={checked}
          onChange={onChange}
        >
        </input>

        <span className="slider"></span>

        <span className="text">{name}</span>
      </label>
    </li>
  );
}
