import React, { useState } from 'react';
import Canvas from './Canvas';

const Planet = props => {
  const {name, radius, color, velocity, orbitRadius} = props;


  const update = ctx => {
    setTheta(theta + velocity);

    setX(starX + orbitRadius * Math.cos(theta));
    setY(starY + orbitRadius * Math.sin(theta));

    draw(ctx);
  }

  const draw = (ctx, frameCount, stageWidth, stageHeight) => {
    ctx.clearRect(0, 0, stageHeight, stageHeight);
    ctx.beginPath();
    ctx.arc(stageWidth/2, stageHeight/2, 20 * Math.sin(frameCount*0.05)**2, 0, Math.PI * 2);
    ctx.fillStyle = 'white';
    ctx.fill();
    ctx.closePath();
  }
  return <Canvas draw={draw} type='dynamic' id='main'/>
}

export default Planet;
